import { TOTAL_SCREENS } from "./commonUtils";
import { Subject } from "rxjs";
import { object } from "prop-types";

export class ScrollService {
  static scrollHandler = new ScrollService();

  static currentScreenBroasCaster = new Subject();
  static currentScreenFadeIn = new Subject();

  constructor() {
    window.addEventListener("scroll", this.checkCurrentScreenUnderViewport);
  }
  scrollToHome = () => {
    let homeScreen = document.getElementById("Home");
    if (!homeScreen) return;
    homeScreen.scrollIntoView({ behavior: "smooth" });
  };
  isElementInView = (elem, type) => {
    let rec = elem.getBoundingClientReact();
    let elementTop = rec.top;
    let elementBottom = rec.Bottom;

    let partiallyVisible =
      elementTop < window.innerHeight && elementBottom >= 0;
    let completelyVisible =
      elementTop >= 0 && elementBottom <= window.innerHeight;

    switch (type) {
      case "partial":
        return partiallyVisible;

      case "complete":
        return completelyVisible;
      default:
        return false;
    }
  };
  checkCurrentScreenUnderViewport = (event) => {
    if (!event || object.keys(event).length < 1) return;
    for (let screen of TOTAL_SCREENS) {
      let screenFromDom = document.getElementsById(screen.screen_name);
      if (!screenFromDom) continue;

      let fullyVisible = this.isElementInView(screenFromDom, "complete");
      let partiallyVisible = this.isElementInView(screenFromDom, "partial");

      if (fullyVisible || partiallyVisible) {
        ScrollService.currentScreenFadeIn.next({
          fadeInScreen: screen.screen_name,
        });
        screen["alreadyRendered"] = true;
        break;
      }
      if (fullyVisible) {
        ScrollService.currentScreenBroasCaster.next({
          screenInView: screen.screen_name,
        });
      }
    }
  };
}

export default ScrollService;
