import ScreenHeading from "../../utilities/ScrennHeading/ScreenHeading";
import ScrollService from "./../../utilities/ScrollService";
import Animations from "../../utilities/Animations";
import { useState } from "react";

import "./Resume.css";

function Resume(props) {
  const [selectedBulletIndex, setSelectedBulletIndex] = useState();
  const [carousalOffSetStyle, setCarousalOffSetStyle] = useState({});

  const resumeHeading = (props) => {
    <div className="resume-heading">
      <div className="resume-main-heaing">
        <div className="heading-bullet">
          <span>{props.heaing ? props.heaing : ""}</span>
          {props.fromDate && props.toDate ? (
            <dov className="heading-date">
              {props.fromDate + "_" + props.toDate}
            </dov>
          ) : (
            <div></div>
          )}
        </div>
        <div className="resume-sub-heading">
          <span>{props.subHeading ? props.subHeading : ""}</span>
        </div>
        <div classname="resume-heading-description">
          <span>{props.description ? props.description : ""}</span>
        </div>
      </div>
    </div>;
  };

  const resumeBullets = [
    { laebel: "Education", logoSrc: "education.svg" },
    { laebel: "Work History", logoSrc: "work-history.svg" },
    { laebel: "Programming Skills", logoSrc: "programming-skills.svg" },
    { laebel: "Projects", logoSrc: "projects.svg" },
    { laebel: "Interests", logoSrc: "interests.svg" },
  ];

  const programmingSkillDetails = [
    { skill: "Javascript", ratingPercentage: 85 },
    { skill: "Javascript", ratingPercentage: 85 },
    { skill: "Javascript", ratingPercentage: 85 },
    { skill: "Javascript", ratingPercentage: 85 },
    { skill: "Javascript", ratingPercentage: 85 },
    { skill: "Javascript", ratingPercentage: 85 },
    { skill: "Javascript", ratingPercentage: 85 },
    { skill: "Javascript", ratingPercentage: 85 },
  ];

  const projectDetails = [
      {
          title:'Personal Portfólio Website',
          duration: {fromDate:'2020', toDate: '2021'},
          description: 'A personal finance and investment portfolio organizer.',
          subHeading: 'React JS, Node.js, Express.js, SCSS, MongoDB, Mongoose, JSX, React charts, Alpha Vantage API'
      },
      {
        title:'Personal Portfólio Website',
        duration: {fromDate:'2020', toDate: '2021'},
        description: 'A personal finance and investment portfolio organizer.',
        subHeading: 'React JS, Node.js, Express.js, SCSS, MongoDB, Mongoose, JSX, React charts, Alpha Vantage API'
    },
    {
        title:'Personal Portfólio Website',
        duration: {fromDate:'2020', toDate: '2021'},
        description: 'A personal finance and investment portfolio organizer.',
        subHeading: 'React JS, Node.js, Express.js, SCSS, MongoDB, Mongoose, JSX, React charts, Alpha Vantage API'
    },
  ]

  let fadeInScreenHandler = (screen) => {
    if (screen.fadeScreen !== props.id) return;
    Animations.animations.fadeInScreen(props.id);
  };
  const fadeInSubscription =
    ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler);

  return (
    <div className="resume-container screen-container" id={props.id || ""}>
      <div className="resume-content">
        <ScreenHeading title={"Resume"} subHeading={"My Formal Bio Details"} />
      </div>
    </div>
  );
}

export default Resume;
